package constants

const (
	SelectQueryDocs = `select top 2 d.CIA, d.ID_PLANTA,pla.DIRECCION AS ID_PLANTA_DIRECCION, CASE WHEN d.ID_TIPO_DOC = 'N/C' THEN 'N-C' WHEN d.ID_TIPO_DOC = 'N/D' THEN 'N-D' ELSE d.ID_TIPO_DOC END AS ID_TIPO_DOC, right(d.SERIE, 3) AS SERIE, d.NRO_DOCUMENTO, d.SUBTOTAL, convert(varchar(10), d.FECHA, 120)AS FECHA,convert(varchar(8), d.FECHA_SISTEMA, 108)as HORA, d.ID_MONEDA_DOC, d.ID_CONDICION_PAGO, c.NRO_DI, c.DESCRIPCION AS DESCRIPCION_CLIENTE,
	c.ID_DISTRITO, c.DIRECCION AS DIRECCION_CLIENTE, dep.DESCRIPCION AS DEPARTAMENTO_CLIENTE, prov.DESCRIPCION AS PROVINCIA_CLIENTE, dist.DESCRIPCION AS DISTRITO_CLIENTE, isnull(c.RAZON_SOCIAL, c.DESCRIPCION) AS RAZON_SOCIAL_CLIENTE ,
	pv.FAX1 AS EMAIL_CLIENTE, convert(varchar(10), d.FECHA_VENCIMIENTO, 120) AS FECHA_VENCE, convert(varchar, d.IGV)as IGV, convert(varchar, d.PORC_IGV)as PORC_IGV, convert(varchar, d.TOTAL) as TOTAL, m.DESCRIPCION as MONEDA_DESCRIPCION, d.ID_TIPO_DOC_REF, right(d.SERIE_REF, 3) AS SERIE_REF, d.NRO_DOCUMENTO_REF, d.ID_PLANTA_REF, mna.DESCRIPCION AS MOTIVO_TIPO_SUNAT, mna.ID_MOTIVO_TIPO_SUNAT AS ID_MOTIVO_TIPO_SUNAT, dna.DESCRIPCION_NOTA, case when c.ID_TIPO_DI = '01' then '1' else '6' end AS ID_TIPO_DI, isnull(convert(varchar, convert(decimal(13,2), d.MONTO_PER)),0) as MONTO_PER, convert(varchar, convert(decimal(13,2), d.TOTAL+d.MONTO_PER)) as TOTAL_MONTO_PER , et.RAZON_SOCIAL AS RAZON_SOCIAL_TRANSPORTE,
	df.RUC_EMP_TRANSPORTE, ch.DESCRIPCION AS CHOFER_DESCRIP, df.ID_CHOFER, df.ORDEN_COMPRA, df.PLACA_TRACTOR, df.NRO_AUTORIZACION_VEHICULO AS 'NRO_AUTORIZACION', df.DIRECCION_ORIGEN, dio.DESCRIPCION as DEPART_ORIGEN, po.DESCRIPCION AS PROVIN_ORIGEN, do.DESCRIPCION AS DISTRI_ORIGEN,dio.ID_DISTRITO as UBIGEO_ORIGEN, pla.DESCRIPCION AS PLANTAORIGEN, df.DIRECCION_DESTINO, did.DESCRIPCION as DISTRI_DESTINO, pd.DESCRIPCION AS PROVIN_DESTINO, dd.DESCRIPCION AS DEPART_DESTINO,did.ID_DISTRITO AS UBIGEO_DESTINO, cpag.DESCRIPCION AS CONDICION_PAGO, df.NRO_SCOP, df.PLACA_CISTERNA , v.NRO_CUBICACION, convert(varchar, ISNULL(d.MONTO_DESCUENTO, 0)) AS MONTO_DESCUENTO,d.FECHA_SISTEMA ,case when d.PORC_PERCEPCION=2 then '0.02' when d.PORC_PERCEPCION=1 then '0.01' when d.PORC_PERCEPCION=0.5 then '0.005' end AS PORC_PERCEPCION,d.ID_CLIENTE ,(select top 1 canc.ID_TIPO_DOC_CREDITO from CANCELACION_DOCUMENTO canc inner join DOCUMENTO_CREDITO dcred on canc.CIA=dcred.CIA and canc.ID_PLANTA_CREDITO=dcred.ID_PLANTA_CREDITO and canc.ID_TIPO_DOC_CREDITO=dcred.ID_TIPO_DOC_CREDITO and canc.AUTOGENERADO=dcred.AUTOGENERADO where canc.CIA=d.CIA and canc.ID_PLANTA=d.ID_PLANTA and canc.ID_TIPO_DOC=d.ID_TIPO_DOC and canc.SERIE=d.SERIE and canc.NRO_DOCUMENTO=d.NRO_DOCUMENTO order by dcred.FECHA) AS ID_TIPO_DOC_CREDITO ,(select top 1 dcred.NRO_PAGO from CANCELACION_DOCUMENTO canc inner join DOCUMENTO_CREDITO dcred on canc.CIA=dcred.CIA and canc.ID_PLANTA_CREDITO=dcred.ID_PLANTA_CREDITO and canc.ID_TIPO_DOC_CREDITO=dcred.ID_TIPO_DOC_CREDITO and canc.AUTOGENERADO=dcred.AUTOGENERADO where canc.CIA=d.CIA and canc.ID_PLANTA=d.ID_PLANTA and canc.ID_TIPO_DOC=d.ID_TIPO_DOC and canc.SERIE=d.SERIE and canc.NRO_DOCUMENTO=d.NRO_DOCUMENTO order by dcred.FECHA) AS NRO_PAGO ,convert(varchar,d.TOTAL_PESO) AS TOTAL_PESO,(select COUNT(ITEM) from DOCUMENTO_DET where CIA=d.CIA and ID_PLANTA=d.ID_PLANTA and ID_TIPO_DOC=d.ID_TIPO_DOC and SERIE=d.SERIE and NRO_DOCUMENTO=d.NRO_DOCUMENTO) AS 'CantidadItem', cia.PORC_NOS_RETIENEN, c.FLAG_ES_AGENTE_RETENEDOR, convert(varchar, convert(decimal(13,2), (d.TOTAL+ ISNULL(d.MONTO_PER,0)) * ((100-cia.PORC_NOS_RETIENEN) / 100))) AS 'MONTO_RETENEDOR', convert(varchar, convert(decimal(13,2), (d.TOTAL+ ISNULL(d.MONTO_PER,0)) * (cia.PORC_NOS_RETIENEN / 100))) AS 'MONTO_PORC_RETENCION', pv.ID_CLASIFICA_TIPO_NEGOCIO, d.FLAG_CONDICION_PAGO_CREDITO, d.MONTO_APLICA_DEBITO_REF, d.PER_CORRELATIVO_CANCELACION from DOCUMENTO d inner join CLIENTE c on c.CIA=d.CIA and c.ID_CLIENTE=d.ID_CLIENTE inner join PUNTO_VENTA pv on d.CIA=pv.CIA and d.ID_CLIENTE=pv.ID_CLIENTE and d.ID_PUNTO_VENTA=pv.ID_PUNTO_VENTA inner join DEPARTAMENTO dep on dep.ID_PAIS=c.ID_PAIS and dep.ID_DEPARTAMENTO=c.ID_DEPARTAMENTO inner join PROVINCIA prov on prov.ID_PAIS=c.ID_PAIS and prov.ID_DEPARTAMENTO=c.ID_DEPARTAMENTO and prov.ID_PROVINCIA=c.ID_PROVINCIA inner join DISTRITO dist on dist.ID_PAIS=c.ID_PAIS and dist.ID_DEPARTAMENTO=c.ID_DEPARTAMENTO and dist.ID_PROVINCIA=c.ID_PROVINCIA and dist.ID_DISTRITO=c.ID_DISTRITO inner join MONEDA m on m.ID_MONEDA=d.ID_MONEDA_DOC left join DOCUMENTO_NOTA_AJUSTE dna on dna.CIA=d.cia and dna.ID_PLANTA=d.ID_PLANTA and dna.ID_TIPO_DOC=d.ID_TIPO_DOC and dna.SERIE=d.SERIE and dna.NRO_DOCUMENTO=d.NRO_DOCUMENTO left join MOTIVO_NOTA_AJUSTE mna on mna.ID_TIPO_DOC=d.ID_TIPO_DOC and dna.ID_MOTIVO_NOTA=mna.ID_MOTIVO_NOTA left join DOCUMENTO_FACTURA df on df.CIA=d.cia and df.ID_PLANTA=d.ID_PLANTA and df.ID_TIPO_DOC=d.ID_TIPO_DOC and df.SERIE=d.SERIE and df.NRO_DOCUMENTO=d.NRO_DOCUMENTO left join EMPRESA_TRANSPORTE et on et.CIA=df.CIA and et.RUC_EMP_TRANSPORTE=df.RUC_EMP_TRANSPORTE left join CHOFER ch on ch.CIA=d.CIA and ch.ID_CHOFER=df.ID_CHOFER left join DEPARTAMENTO do on do.ID_PAIS=df.id_pais_origen and do.ID_DEPARTAMENTO=df.id_departamento_origen left join PROVINCIA po on po.ID_PAIS=df.id_pais_origen and po.ID_DEPARTAMENTO=df.id_departamento_origen and po.ID_PROVINCIA=df.id_provincia_origen left join DISTRITO dio on dio.ID_PAIS=df.id_pais_origen and dio.ID_DEPARTAMENTO=df.id_departamento_origen and dio.ID_PROVINCIA=df.id_provincia_origen and dio.ID_DISTRITO=df.ID_DISTRITO_origen left join DEPARTAMENTO dd on dd.ID_PAIS=df.ID_PAIS_DESTINO and dd.ID_DEPARTAMENTO=df.id_departamento_DESTINO left join PROVINCIA pd on pd.ID_PAIS=df.id_pais_DESTINO and pd.ID_DEPARTAMENTO=df.id_departamento_DESTINO and pd.ID_PROVINCIA=df.id_provincia_DESTINO left join DISTRITO did on did.ID_PAIS=df.id_pais_DESTINO and did.ID_DEPARTAMENTO=df.id_departamento_DESTINO and did.ID_PROVINCIA=df.id_provincia_DESTINO and did.ID_DISTRITO=df.ID_DISTRITO_DESTINO inner join PLANTA pla on pla.CIA=d.CIA and pla.ID_PLANTA=d.ID_PLANTA left join CONDICION_PAGO cpag on cpag.CIA=d.CIA and cpag.ID_CONDICION_PAGO=d.ID_CONDICION_PAGO left join VEHICULO v on v.CIA=d.CIA and v.PLACA_TRACTOR=df.PLACA_TRACTOR and v.PLACA_CISTERNA=df.PLACA_CISTERNA left join cierre_diario cx ON d.cia = cx.cia AND d.id_planta=cx.id_planta AND d.fecha=cx.fecha inner join CIA cia ON d.CIA = cia.CIA left join DOCUMENTO d2 on d.CIA=d2.CIA and d.ID_PLANTA_REF=d2.ID_PLANTA and d.ID_TIPO_DOC_REF=d2.ID_TIPO_DOC and d.SERIE_REF=d2.SERIE and d.NRO_DOCUMENTO_REF=d2.NRO_DOCUMENTO and d2.FECHA_SISTEMA>='2022-09-05 07:50:00.000' where d.FECHA>='2022-09-01' and d.ID_ESTADO_DOC='01' and d.TICKET_EFACT IS NULL and ( d.ID_TIPO_DOC in ('bol','fac','N/C','N/D') or (d.id_tipo_doc = 'PER' and pv.ID_CLASIFICA_TIPO_NEGOCIO = '01' and d2.FLAG_CONDICION_PAGO_CREDITO = '1' and d.cia = '06' and d.FECHA_SISTEMA >= '2022-09-05 07:50:00.000') ) and ( (d.CIA='07' and isnull(pla.FLAG_ENVIO_AUTOMATICO,0)='1') OR (d.CIA='07' and isnull(pla.FLAG_ENVIO_AUTOMATICO,0)!='1' and cx.FLAG_CERRADO ='1') OR (d.CIA in ('05','06')) ) ORDER BY d.cia, d.FECHA_SISTEMA`

	SelectQueryBajas = `SELECT d.CIA,d.ID_PLANTA,d.ID_TIPO_DOC,d.SERIE,d.NRO_DOCUMENTO,CONVERT(VARCHAR(10), d.FECHA, 120) AS FECHA,
		CONVERT(VARCHAR(10), GETDATE(), 120)AS FECHAACTUAL 
	from DOCUMENTO d
	where d.cia in('05','06','07') 
	and d.ID_TIPO_DOC in('n/c','fac','n/d','bol', 'per') 
	and d.ID_ESTADO_DOC='02' 
	and (d.TICKET_EFACT IS NOT NULL and d.ID_TIPO_DOC in('n/c','fac','n/d','bol') OR 
		(d.ID_TIPO_DOC in('per') and d.TICKET_EFACT IS NOT NULL and 
			(select d2.TICKET_EFACT from DOCUMENTO d2 where d2.CIA=d.cia and d2.ID_PLANTA=d.ID_PLANTA_REF 
			and d2.ID_TIPO_DOC=d.ID_TIPO_DOC_REF and d2.SERIE=d.SERIE_REF and d2.NRO_DOCUMENTO=d.NRO_DOCUMENTO_REF) IS NOT NULL ) 
		)
	AND d.FECHA>='2023-02-01' 
	and (CASE WHEN d.id_tipo_doc ='FAC' OR (d.id_tipo_doc ='N/C' and d.id_tipo_doc_ref='FAC') THEN
			case when DATEDIFF(d, d.fecha, GETDATE()) <= 7 then 'ok' else 'no' end
		WHEN d.id_tipo_doc ='BOL' OR (d.id_tipo_doc ='N/C' and d.id_tipo_doc_ref='BOL') THEN 
			case when DATEDIFF(d, d.fecha, GETDATE()) <= 5 then 'ok' else 'no' end 
		ELSE 'ok'
		END ) = 'ok' 
	AND d.id_planta='00' and ((d.ID_TIPO_DOC='fac' and NRO_DOCUMENTO='00000254') or (d.ID_TIPO_DOC='per' and NRO_DOCUMENTO='00000256'))
	ORDER BY d.ID_TIPO_DOC;`
)
